class MetaDataFilterConstructor(Component):
    display_name = "Search Meta Data Filter Constructor"
    description = "A custom component to allow user to input pre-filter criteria"

    inputs = [
        MessageTextInput(
            name="tribe",
            display_name="Tribe Name",
            info="Enter the ID of a specific tribe"
        ),
        MessageTextInput(
            name="likes_minimum",
            display_name="Minimum likes count",
            info="Minimum number of likes to qualify"
        ),
        MessageTextInput(
            name="platform",
            display_name="Platform (TikTok, Facebook, Twitter or YouTube)",
            info="TikTok, Facebook, Twitter or YouTube"
        ),
        MessageTextInput(
            name="gender",
            display_name="Gender",
            info="Male or Female"
        ),
        MessageTextInput(
            name="age",
            display_name="Age Group (<=18, 19_29, 30_39, 40+)",
            info="Age group demographic"
        ),
        MessageTextInput(
            name="country",
            display_name="Country (UK, US, DE, IN, CN, etc.)",
            info="UK, US, DE, IN, CN, etc."
        ),
        MessageTextInput(
            name="datetime_from",
            display_name="From",
            info="Find posts/comments posted on or after this date (YYYY-MM-DD)"
        ),
        MessageTextInput(
            name="datetime_to",
            display_name="To",
            info="Find posts/comments posted on or before this date (YYYY-MM-DD)"
        ),
        MessageTextInput(
            name="followers",
            display_name="Min. Follower Count",
            info="At least this many followers"
        ),
    ]

    outputs = [
        Output(display_name="Result", name="result", method="process_inputs"),
    ]

    def process_inputs(self) -> Message:
        """
        Create a Vector DB metadata filter.

        Returns:
            Message: A message object containing the processed information.
        """
        try:
            search_filter = []

            if self.likes_minimum is not None and self.likes_minimum != "":
                search_filter.append({
                    "field": "likes",
                    "operator": "gte",
                    "value": int(self.likes_minimum)
                })

            if self.followers is not None and self.followers != "":
                search_filter.append({
                    "field": "follower_count",
                    "operator": "gte",
                    "value": int(self.follower_count)
                })

            if self.tribe is not None and self.tribe != "":
                search_filter.append({
                    "field": "tribe",
                    "operator": "eq",
                    "value": int(self.tribe)
                })

            for field in ['platform', 'gender', 'type', 'country', 'age']:
                value = getattr(self, field, None)
                if value is not None and value != "":
                    search_filter.append({
                        "field": field,
                        "operator": "eq",
                        "value": str(value)
                    })


            if self.datetime_from or self.datetime_to:
                try:
                    if self.datetime_from:
                        datetime_from_obj = datetime.strptime(self.datetime_from, "%Y-%m-%d")
                        search_filter.append({
                            "field": "create_time",
                            "operator": "gte",
                            "value": datetime_from_obj.strftime("%Y-%m")
                        })
                    if self.datetime_to:
                        datetime_to_obj = datetime.strptime(self.datetime_to, "%Y-%m-%d")
                        search_filter.append({
                            "field": "create_time",
                            "operator": "lte",
                            "value": datetime_to_obj.strftime("%Y-%m")
                        })
                except ValueError:
                    return Message(text="Invalid datetime format. Please use YYYY-MM-DD.")

            if not search_filter:
                return Message(text="No filter criteria provided.")

            return Message(text=json.dumps(search_filter, indent=4))
        except AttributeError as e:
            return Message(text=f"Error processing inputs: {str(e)}")

